// Dependencies
import { React, useState } from "react";
import axios from 'axios';
import "../css/PopularCities.css";


export default function PopularCities({ cities }) {
  const [nameOfCity, setNameOfCity] = useState('');

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${nameOfCity}&units=imperial&appid=f0f89e0388f82c90f094a7a9a8675497`;

  const cityWeather = []

  const setHomeWeathers = (cityArr) => {

    cityArr.forEach(city => {
      setNameOfCity(prevState => {prevState = city})
      axios.get(url).then((response) => {
        cityWeather.push(response.data.main.temp)
        console.log(respon)

      });
    })
  }
  setHomeWeathers(cities)

  return (
      <div className="home-container">
        <section className="city-card">
          <div className="popular-searches">
            {cities?.map((city) => (
              <div className="city-info">
                <p className="city-name" id="city-name">{city.location}</p>
                <img className="skyline" id="city-skyline" src={city.image} alt="skyline"/>
              </div>
            ))}
          </div>
        </section>
      </div>
  )
}
