import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { React, useState, useEffect } from "react";
import { getAllCities } from "./services/city";
import PopularCities from "./components/PopularCities";
import CityWeather from "./components/CityWeather";
import Layout from "./components/Layout";

export default function App() {
  const [cities, setCities] = useState([]);
  const [data, setData] = useState({});
  const [location, setLocation] = use


  useEffect(() => {
    const fetchCities = async () => {
      const cityList = await getAllCities();
      setCities(cityList);
      console.log(cityList);
    };
    fetchCities();
  }, []);

  // const url = `https://api.openweathermap.org/data/2.5/weather?q={city name}&appid=f0f89e0388f82c90f094a7a9a8675497`



  return (
    <div className="app">

          {/* <Layout> */}
        <Router>
        <Routes>
          <Route path="/" element={<PopularCities cities={cities} />}></Route>
          <Route path="/result" element={<CityWeather/>}></Route>
          </Routes>
        </Router>
          {/* </Layout> */}

    </div>
  );
}
