import "../css/CityWeather.css";


export default function CityWeather({ data }) {
  return (
    <div className="container">
      <div className="left-column">
        <div className="location">
          <p className="data-name">{data.name}</p>
          {data.sys ? <p className="data-country">{data.sys.country}</p> : null}
        </div>

        <div className="temp">
          {data.main ? <h2>{data.main.temp.toFixed()}째F</h2> : null}
        </div>
        <div className="description">
          {data.weather ? <p className="type">{data.weather[0].main}</p> : null}
        </div>
      </div>
      {data.name !== undefined && 
        <div className="right-column">
          <div className="feels">
            <p>Feels like</p>
            {data.main ? <p>{data.main.feels_like.toFixed()}째F</p> : null}
          </div>
          <div className="humidity">
            <p>Humidity</p>
            {data.main ? <p>{data.main.humidity}%</p> : null}
          </div>
          <div className="wind">
            <p>Wind Speed</p>
            {data.wind ? <p>{data.wind.speed.toFixed()} mph</p> : null}
          </div>

            <div className="temp-min">
              <p>Low</p>
              {data.main ? <p>{data.main.temp_min.toFixed()}째F</p> : null}
            </div>
            <div className="temp-max">
              <p>High</p>
              {data.main ? <p>{data.main.temp_max.toFixed()}째F</p> : null}
            </div>

  </div>
  } 
    </div>
  );
}
