import "./App.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { React, useState, useEffect } from "react";
import { getAllCities } from "./services/city";
import PopularCities from "./components/PopularCities";
import CityWeather from "./components/CityWeather";
import axios from "axios";
import Layout from "./components/Layout";

export default function App() {
  const [cities, setCities] = useState([]);
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');


  useEffect(() => {
    const fetchCities = async () => {
      const cityList = await getAllCities();
      setCities(cityList);
      console.log(cityList);
    };
    fetchCities();
  }, []);

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=f0f89e0388f82c90f094a7a9a8675497`

  const searchForLocation = (ev) => {
    if (ev.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
    }
  }
  return (
    <div className="app">


        <Router>
        <Routes>
          <Route path="/" element={<PopularCities cities={cities} />}></Route>
          <Route path="/result" element={<CityWeather/>}></Route>
          </Routes>
        </Router>


    </div>
  );
}
